// ** JWT import
import jwt from 'jsonwebtoken'

// ** Mock Adapter
import mock from 'src/@fake-db/mock'

// ** Default AuthConfig
import defaultAuthConfig from 'src/configs/auth'

const users = [
    {
        id: 1,
        role: 'admin',
        password: 'admin',
        fullName: 'John Doe',
        username: 'johndoe',
        email: 'admin@gmail.com',
        paying: true,
        photo: 'data:image/png;base64,'
    },
    {
        id: 2,
        role: 'client',
        password: 'client',
        fullName: 'Jane Doe',
        username: 'janedoe',
        email: 'client@gmail.com',
        paying: false,
        photo: 'data:image/png;base64,'
    }
]

const jwtConfig = {
    secret: process.env.NEXT_PUBLIC_JWT_SECRET,
    expirationTime: process.env.NEXT_PUBLIC_JWT_EXPIRATION,
    refreshTokenSecret: process.env.NEXT_PUBLIC_JWT_REFRESH_TOKEN_SECRET
}

// ** Login User
mock.onPost('/jwt/login').reply(request => {
    const { email, password } = JSON.parse(request.data)

    let error = {
        email: ['Algo deu errado']
    }
    const user = users.find(u => u.email === email && u.password === password)
    if (user) {
        const accessToken = jwt.sign({ id: user.id }, jwtConfig.secret, { expiresIn: jwtConfig.expirationTime })

        const response = {
            accessToken,
            userData: { ...user, password: undefined }
        }

        return [200, response]
    } else {
        error = {
            email: ['E-mail ou senha é inválido']
        }

        return [400, { error }]
    }
})

// ** Register User
mock.onPost('/jwt/register').reply(request => {
    if (request.data.length > 0) {
        const { email, password, username } = JSON.parse(request.data)
        const isEmailAlreadyInUse = users.find(user => user.email === email)
        const isUsernameAlreadyInUse = users.find(user => user.username === username)

        const error = {
            email: isEmailAlreadyInUse ? 'Esse email já está em uso.' : null,
            username: isUsernameAlreadyInUse ? 'Este nome de usuário já está em uso.' : null
        }

        if (!error.username && !error.email) {
            const { length } = users
            let lastIndex = 0
        if (length) {
            lastIndex = users[length - 1].id
        }

        const userData = {
            id: lastIndex + 1,
            email,
            password,
            username,
            photo: null,
            fullName: '',
            paying: false,
            role: 'client'
        }

        users.push(userData)
        const accessToken = jwt.sign({ id: userData.id }, jwtConfig.secret)
        const user = { ...userData }
        delete user.password
        const response = { accessToken }

        return [200, response]
        }

        return [200, { error }]
    } else {
        return [401, { error: 'Dados inválidos' }]
    }
})

mock.onGet('/auth/me').reply(config => {
    // ** Obter token do Header
    const token = config.headers.Authorization

    // ** Resposta padrão
    let response = [200, {}]

    // ** Verifica se o token é válido ou expirou
    jwt.verify(token, jwtConfig.secret, (err, decoded) => {
        // ** Se o token estiver expirado
        if (err) {
        // ** Se onTokenExpiration === 'logout', envie o erro 401
        if (defaultAuthConfig.onTokenExpiration === 'logout') {
            // ** A resposta 401 desconectará o usuário do arquivo AuthContext
            response = [401, { error: { error: 'Usuário Inválido' } }]
        } else {
            // ** Se onTokenExpiration === 'refreshToken', gere o novo token
            const oldTokenDecoded = jwt.decode(token, { complete: true })

            // ** Obtenha o ID do usuário do token antigo
            const { id: userId } = oldTokenDecoded.payload

            // ** Obter usuário que corresponda ao id no token
            const user = users.find(u => u.id === userId)

            // ** Assinar um novo token
            const accessToken = jwt.sign({ id: userId }, jwtConfig.secret, {
                expiresIn: jwtConfig.expirationTime
            })

            // ** Definir novo token no localStorage
            window.localStorage.setItem(defaultAuthConfig.storageTokenKeyName, accessToken)
            const obj = { userData: { ...user, password: undefined } }

            // ** retorna 200 com dados do usuário
            response = [200, obj]
        }
        } else {
        // ** Se o token for válido, não faça nada
        const userId = decoded.id

        // ** Obter usuário que corresponda ao id no token
        const userData = JSON.parse(JSON.stringify(users.find(u => u.id === userId)))
        delete userData.password

        // ** retorna 200 com dados do usuário
        response = [200, { userData }]
        }
    })

    return response
})
